import React from 'react';
import { useRouter } from 'next/router';
import Link from 'next/link';
import axios from 'axios';
import { makeStyles } from '@material-ui/styles';
import { Container, Grid } from '@material-ui/core';

const bioStyles = makeStyles({
  title: {
    fontWeight: 'bold',
  },
});

function characterProfile(props) {
  const styles = bioStyles();

  const router = useRouter();
  const id = router.query;

  return (
    <Container style={{ padding: '1em' }}>
      <div style={{ margin: '2em 0em' }}>
        <Link href='/'>
          <a className={styles.title}>Home</a>
        </Link>
        <span> / {props.data.name}</span>
      </div>

      <Grid
        container
        justify='center'
        direction='column'
        alignItems='center'
        spacing={2}
      >
        {' '}
        <img src={props.data.image} alt={props.data.name} />
        <p>{props.data.name}</p>
        <p>
          <span className={styles.title}>Status: </span>
          {props.data.status}
        </p>
        <p>
          <span className={styles.title}>Gender: </span>
          {props.data.gender}
        </p>
        <p>
          <span className={styles.title}>Origin: </span>
          {props.data.origin.name}
        </p>
        <p>
          <span className={styles.title}>Last known location: </span>
          {props.data.location.name}
        </p>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Consequuntur
          impedit tempore, perferendis sint consectetur corrupti sunt aperiam
          deleniti veritatis non incidunt, debitis distinctio ut numquam
          pariatur nisi architecto, neque iusto?
        </p>
        <Link href='/'>
          <button>Go back</button>
        </Link>
      </Grid>
    </Container>
  );
}

characterProfile.getInitialProps = async (ctx) => {
  let res = await axios.get(
    `https://rickandmortyapi.com/api/character/${ctx.query.id}`
  );
  let data = res.data;
  console.log(data);

  return { data };
};

export default characterProfile;
