import React from 'react';
import axios from 'axios';
import { useRouter } from 'next/router';
import Link from 'next/link';
import { Grid } from '@material-ui/core';
import MainContainer from '../../components/MainContainer';
import StatusBar from '../../components/StatusBar';

const idCharacterStyles = makeStyles({
  root: {
    display: 'flex',
    justifyContent: 'center',
  },
  input: {
    width: 'auto',
    height: '1.5em',
    borderRadius: '10px',
    border: 'solid #159857 2px',
  },
  img: {
    width: '3.5em',
    position: 'relative',
    top: '-1em',
  },
});

function characterProfile(props) {
  const styles = idCharacterStyles();
  const router = useRouter();
  const id = router.query;

  return (
    <MainContainer>
      <div
        style={{
          margin: '2em 0em',
          fontFamily: 'Roboto',
        }}
      >
        <p
          style={{
            color: 'black',
            margin: '2em 0',
            textAlign: 'center',
          }}
        >
          <Link href='/'>
            <a style={{ color: 'black' }}>Home</a>
          </Link>
          <span
            style={{
              fontWeight: 'bold',
            }}
          >
            {' '}
            / {props.data.name}
          </span>
        </p>

        <Grid
          item
          xs={10}
          sm={8}
          md={6}
          lg={6}
          style={{
            flexGrow: 1,
            borderRadius: '20px',
            textAlign: 'center',
            justifySelf: 'center',
            margin: 'auto',
            boxShadow: '4px 2px 5px -3px #000000',
          }}
        >
          <div
            style={{
              backgroundColor: '#ebe8ce',
              borderRadius: '20px',
              padding: '1.5em',
            }}
          >
            <img
              src={props.data.image}
              alt={props.data.name}
              width='60%'
              style={{
                borderRadius: '50%',
              }}
            />
            <p
              style={{
                fontWeight: 'bold',
              }}
            >
              {props.data.name}
            </p>
            <div>
              <span
                style={{
                  fontWeight: 'bold',
                }}
              >
                Status:
              </span>
              <StatusBar
                status={props.data.status}
                style={{ width: '50%', color: 'white' }}
              />
            </div>
            <p>
              <p
                style={{
                  fontWeight: 'bold',
                }}
              >
                Gender:
              </p>
              {props.data.gender}
            </p>
            <p>
              <p
                style={{
                  fontWeight: 'bold',
                }}
              >
                Origin:
              </p>
              {props.data.origin.name}
            </p>
            <p>
              <p
                style={{
                  fontWeight: 'bold',
                }}
              >
                Last known location:
              </p>
              {props.data.location.name}
            </p>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Consequuntur impedit tempore, perferendis sint consectetur
              corrupti sunt aperiam deleniti veritatis non incidunt, debitis
              distinctio ut numquam pariatur nisi architecto, neque iusto?
            </p>
          </div>
        </Grid>

        <Link href='/'>
          <button
            style={{
              background: '#155799',
              width: '8em',
              height: '2em',
              display: 'block',
              marginLeft: 'auto',
              marginRight: 'auto',
              marginTop: '2em',
              border: 'none',
              borderRadius: '10px',
              color: 'white',
            }}
          >
            Go back
          </button>
        </Link>
      </div>
    </MainContainer>
  );
}

characterProfile.getInitialProps = async (ctx) => {
  let res = await axios.get(
    `https://rickandmortyapi.com/api/character/${ctx.query.id}`
  );
  let data = res.data;
  console.log(data);

  return { data };
};

export default characterProfile;
