import React from 'react';
import { makeStyles } from '@material-ui/styles';

const StatusBarStyles = makeStyles({
  root: {
    width: '2em',
  },
});

function StatusBar({ status }) {
  const styles = StatusBarStyles();

  switch (status) {
    case 'Alive':
      return (
        <div className={styles.root} style={{ backgroundColor: 'red' }}>
          {status}
        </div>
      );
    case 'Dead':
      return <div style={{ backgroundColor: 'purple' }}>{status}</div>;
    case 'unknown':
      return <div style={{ backgroundColor: 'green' }}>{status}</div>;
  }
}

export default StatusBar;
